package simple1;

public class swapNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int temp;
		System.out.println("Before swap a = " + a + " b = " +b);
		temp = a;
		a = b;
		b = temp;
		System.out.println("After swap a = " + a + " b = " +b);
		

	}

}
//5.	Write a Java program to swap two variables.