package simple1;

public class swapTwoNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 20;
		int b = 10;
		System.out.println("Before swap a = " + a + " b = " +b);
		a = a + b; // 30
		b = a - b; // 20
		a = a - b; // 10
		System.out.println("After swap a = " + a + " b = " +b);

	}

}
